function getMaxValue(str, k) {
  let n = str.length;
  let dp = new Array(n + 1).fill(0).map(() => new Array(k + 1).fill(Infinity));

  dp[0][0] = 0;

  for (let i = 1; i <= n; i++) {for (let j = 1; j <= k; j++) {for (let p = i - 1; p>= 0; p--) {let subStr = str.substring(p, i);
        let uniqueChars = new Set(subStr);
        let value = subStr.length * uniqueChars.size;
        dp[i][j] = Math.min(dp[i][j], Math.max(dp[p][j - 1], value));
      }
    }
  }
  return dp[n][k];
}
