    int rob(vector<int>& nums) {
        if (nums.empty()) {
            return 0;
        }
        else if (nums.size() == 1) {
            return nums[0];
        }
        else if (nums.size() == 2) {
            return nums[0] > nums[1] ? nums[0] : nums[1];
